mapper.select:
SELECT  FROM CNT_INFO 


	AND CNT_ID =#{cntId}


	AND CNT_TITLE LIKE CONCAT('%', #{title}, '%')


	AND CNT_TITLE_EN LIKE CONCAT('%', #{title_EN}, '%')


	AND CNT_STATUS =#{status}


	AND CNT_MODEL =#{model}


	AND CNT_TYPE =#{type}


	AND INSERT_TIME <= #{end}


	AND INSERT_TIME >= #{start}


	AND USER_ID = F_USER_ID(#{user})



	ORDER BY ${sort} ${order}

SELECT  FROM CNT_INFO WHERE CNT_STATUS =1 

AND CNT_MODEL =#{model}


AND CNT_TYPE =#{type}

ORDER BY INSERT_TIME DESC, CNT_ID DESC LIMIT 0, ${max}

SELECT F_MT_NAME(CNT_TYPE) name, count(1) value FROM CNT_INFO WHERE month(INSERT_TIME)=#{month}

	AND USER_ID=#{userId} 

GROUP BY CNT_TYPE
