mapper.select:
SELECT  FROM INST_INFO 


	AND INST_ID =#{instId}


	AND INST_TITLE LIKE CONCAT('%', #{title}, '%')


	AND INST_TYPE =#{type}


	AND INSERT_TIME <= #{end}


	AND INSERT_TIME >= #{start}


	AND USER_ID = F_USER_ID(#{user})



	ORDER BY ${sort} ${order}

SELECT  FROM INST_INFO WHERE

AND INST_TYPE =#{type}

ORDER BY UPDATE_TIME DESC, CNT_ID DESC LIMIT 0, ${max}
