mapper.select:
SELECT  FROM USER_INFO

	USER_NAME <> 'admin'

	AND USER_NAME LIKE CONCAT('%', #{userName}, '%')


	AND USER_STATUS=#{status}


	AND F_ROLE_ID(USER_ID)=#{roleId}


	AND CERT_SN=#{sn}


	AND INSERT_TIME <= #{end}


	AND INSERT_TIME >= #{start}


	AND USER_ID = #{userId}



	ORDER BY ${sort} ${order}
